import React,{Component} from "react";

class CardTemp extends Component {
  constructor(props){
    super(props);
    const img ='';
    console.log(img)
  }
  async componentDidMount(){
    //
    const url = "http://localhost:8080/Accueil/events";
    const response = await fetch(url);
    const data = await response.json().then(results=> this.setState({'data':results}))
    //    
 
}
  componentWillMount(){
    this.img= require("../uploads/events/" + this.props.img);
  }
  render(props) { 
    return (
    
            <div className="col-lg-4 col-sm-6 portfolio-item">
              <div className="card h-100">
                <a href="#"><img className="card-img-top" src={this.img}  alt=""/></a>
                <div className="card-body">
                  <h4 className="card-title">
                    <a href="#"> {this.title}</a>
                  </h4>
                  <p className="card-text"> {this.props.description}</p>
                </div>
            </div>
        </div> 
        
    );
  }
}
 
export default CardTemp;
