import React,{Component} from "react";
import "antd/dist/antd.css";
import '../assets/css/Home.css'
import '../assets/css/modern-business.css'

//import SlideShow from 'react-image-show';
import CardTemp from "../components/CardTemp";
import Slid from "../components/Slid";

class Home extends Component {
    constructor(){
        super();
        this.state =
            {
                'events': [],
                'venteFlash':[],

            }
       //const events=[];
        //const backgroundimg ={'backgroundImage':'http://placehold.it/1900x1080'};


    }
    componentDidMount(){
        
        let events,venteFlash,url,url1,response,response1;
       
        // events
        url = "http://localhost:8080/Accueil/events";
        //response =  fetch(url);
        events  =  fetch(url).then(response.json().then(results=> {return results}));
        //    venteFlash
        url1 = "http://localhost:8080/Accueil/venteFlash";
        //response1 =  fetch(url1).then
        venteFlash  =  fetch(url1).then(response1.json().then(results=> {return results}));
        
        this.setState({
            'events' : events,
            'venteFlash': venteFlash

        });
        
     
    }
    render() { 
        console.log(this.state.venteFlash);
        return (
               < div>
                        
                        <Slid venteFlash = {this.state.venteFlash}/>
                        <div className="jumbotron">
                            <h1 className="display-3">Actualité et évènement</h1>
                            <p className="lead">This is a sntent or information.</p>
                            <hr/>
                            <p>It uses utility classNamees for typography and ent out within the larger container.</p>
                          
                            <div className="row">
                                {   this.state.events.map(function(event)
                                        {
                                            return (
                                                <CardTemp key={event.id} description={event.description} title={event.title} img={event.fileName}/>

                                            )
                                        })
                                }
                                
                            </div>

                        </div>

                </div>
                 );
  }

}
export default Home;
