import React,{Component} from "react";
import {Carousel} from "antd";
import "antd/dist/antd.css";
class Slid extends Component {
   constructor(props){
      super(props);
      this.state={
         venteFlash:[],
      }

   }
   async componentDidMount(){
        
      let url1,response1,venteFlash;
     
      //    venteFlash
      url1 = "http://localhost:8080/Accueil/venteFlash";
      response1 = await fetch(url1);
      venteFlash  = await response1.json().then(results=> {return results});
      
      this.setState({
          'venteFlash' : venteFlash,

      });
      console.log(this.state.venteFlash);
   
  }


   render(){
      console.log(this.state.venteFlash);

            return (

            <Carousel autoplay>
                     {   
                        this.state.venteFlash.map(function(image)
                                        {
                                            return (
                                                   <div> 
                                                      <img className="card-img-top" src={image.fileName}  alt=""/>
                                                   </div>
                                            )
                                        })
                                
                     
                     }
                           

            </Carousel>);
   }
}
 export default Slid;
