import React,{Component} from "react";
import {Carousel} from "antd";
import "antd/dist/antd.css";
class Slid extends Component {
   constructor(props){
      super(props);
      this.state={
         'images':[],
      }

   }
   componentWillMount(){
    
      this.props.images.map(function(image)
      {
                      this.setState((prevState)=> ({ images: prevState.images.push(
                        require("../uploads/venteFlash/" + image.fileName)
                        )}) 
                      );
     });
     console.log('mi vente flash',images);
   }


   render(){
            return (

            <Carousel autoplay>
                     {   
                        this.state.images.map(function(image)
                                        {
                                            return (
                                                   <div> 
                                                      <img className="card-img-top" src={image}  alt=""/>
                                                   </div>
                                            )
                                        })
                                
                     
                     }
                           

            </Carousel>);
   }
}
 export default Slid;
