import React,{Component} from "react";
import "antd/dist/antd.css";
import '../assets/css/Home.css'
import '../assets/css/modern-business.css'
import "../assets/css/Slid.css"
import ReadMoreReact from 'read-more-react';
//import SlideShow from 'react-image-show';
import CardTemp from "../components/CardTemp";
import Slid from "../components/Slid";
class Home extends Component {
    
    constructor(){
        super();
        this.state =
            {
                'events': [],

            }
       
        //const backgroundimg ={'backgroundImage':'http://placehold.it/1900x1080'};
        this.divStyle = {
            marginTop : '100px',
            
          }
        this.taile ={
            min: 10,
            max : 100,
            ideal: 30,
            button:"<div>lala</div>",
            text:"Lorem ipsum dolor sit amet consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam,quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat. Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur. Excepteur sint occaecat cupidatat non proidentsunt in culpa qui officia deserunt mollit anim id est laborum.",
        };


    }
    async componentDidMount(){
        
        let events,url,response;
       
        // events
        url = "http://localhost:8080/Accueil/events";
        response = await fetch(url);
        events  = await response.json().then(results=> {return results});
     
        
        this.setState({
            'events' : events,

        });
        
     
    }
    render() { 
        return (
               < div>
                        
                        <Slid />
                      
                        <div className="jumbotron " style={this.divStyle}>
                            <h1 className="display-3">Actualité et évènement</h1>
                            <p className="lead">This is a sntent or information.</p>
                            <hr/>
                            <p>It uses utility classNamees for typography and ent out within the larger container.</p>
                          
                            <div className="row">
                                {   this.state.events.map(function(event)
                                        {
                                            return (
                                                <CardTemp key={event.id} description={event.description} title={event.title} img={event.fileName}/>

                                            )
                                        })
                                }
                                
                            </div>
                    
                    <ReadMoreReact text={this.taile.text}
                                min={this.taile.min}
                                ideal={this.taile.ideal}
                                max={this.taile.max}
                                readMoreText={<a className="btn btn-primary btn-lg" href="#" role="button">Learn more</a>}/>
                        </div>
                  

                </div>
                 );
  }

}
export default Home;
