import React, { Component } from 'react';

import '../assets/css/Signup.css';
import { Link } from 'react-router-dom';


import { Form, Input, DatePicker,Button,Upload,Icon } from 'antd';
const FormItem = Form.Item;

class addEvent extends Component {
    constructor(props) {
        super(props);
        this.state = {
            title: {
                value: ''
            },
            file: {
                value: ''
            },
            content: {
                value: ''
            },
            date: {
                value: ''
            },
            description: {
             value: ''
         }

        }
        const config = {
         rules: [{ type: 'object', required: true, message: 'Please select time!' }],
       };
     
        this.handleInputChange = this.handleInputChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
        this.isFormInvalid = this.isFormInvalid.bind(this);
        this.onChangeDate=this.onChangeDate.bind(this);
        this.FileUploadchange=this.FileUploadchange.bind(this);
       
          
    }

    handleInputChange(event, validationFun) {
        const target = event.target;
        const inputName = target.name;        
        const inputValue = target.value;

        this.setState({
            [inputName] : {
                value: inputValue,
                ...validationFun(inputValue)
            }
        });
    }

    handleSubmit(event) {
        event.preventDefault();
        
    }

    isFormInvalid() {
        return !(this.state.description.validateStatus === true &&
            this.state.file.validateStatus === true && this.state.content.validateStatus === true
            && this.state.title.validateStatus === true && this.state.date.validateStatus === true
           
        );
    }
    onChangeDate(date, dateString) {
    if(dateString.trim()=="")
    {
            this.setState({
                'date' : {
                'value' : null,
                'valueString':null,
                'validateStatus': false,
                'errorMsg': 'Empty value',
                },
            });
    }
    else{
        this.setState({
            'date' : {
            'value' : date,
            'valueString':dateString,
            'validateStatus': true,
            'errorMsg': null,
            },
        });
    }
     
   }
   FileUploadchange(infs){
        const file = infs.file;
        switch (infs.file.status) {
            case "uploading":
                this.setState({
                    'file' : {
                        'value' : null,
                        'fileList': [file],
                        'validateStatus': false,
                        'errorMsg': "uploading",
                       
                    }
                });
              break;
            case "done":
                this.setState({
                    'file' : {
                        'value' : file ,
                        'fileList': [file] ,
                        'validateStatus': true ,
                        'errorMsg': null ,
                       
                    }
                });
              break;
      
            default:
                this.setState({
                    'file' : {
                        'value' : null ,
                        'fileList': [] ,
                        'validateStatus': false ,
                        'errorMsg': 'upload failed' ,
                       
                    }
                });
  
        }

        
        console.log(infs);
   }
   
    render() {
  
        return (
            <div className="signup-container">
                <h1 className="page-title">Ajout d'un événement ou actualité :</h1>
                <div className="signup-content">
                    <Form onSubmit={this.handleSubmit} className="signup-form">
                        <FormItem 
                            label="Titre :"
                            validateStatus={this.state.title.validateStatus}
                            help={this.state.title.errorMsg}>
                            <Input 
                                size="large"
                                name="title"
                                autoComplete="off"
                                placeholder="Titre ..."
                                value={this.state.title.value} 
                                onChange={(event) => this.handleInputChange(event, this.validateTitle)} />    
                        </FormItem>
                        <FormItem 
                            label="Description :"
                            validateStatus={this.state.description.validateStatus}
                            help={this.state.description.errorMsg}>
                            <Input 
                                size="large"
                                name="description"
                                autoComplete="off"
                                placeholder="description ..."
                                value={this.state.description.value} 
                                onChange={(event) => this.handleInputChange(event, this.validateDescription)} />    
                        </FormItem>
                       
                        
                        <FormItem 
                            label="Date de Clôture :"
                            validateStatus={this.state.date.validateStatus}
                            help={this.state.date.errorMsg}>
                          
                               <DatePicker 
                                size="large"
                                name="date"                
                                value={this.state.date.value} 
                                onChange={this.onChangeDate} />
                               
                        </FormItem>
                        <FormItem 
                            label="Image :"
                            validateStatus={this.state.file.validateStatus}
                            help={this.state.file.errorMsg}>
                            <Upload
                                accept=".png,.jpg,.jpeg"
                                listType='picture'
                                fileList={this.state.file.fileList}
                                onChange={this.FileUploadchange}
                                multiple={false}
                                

                            >
                                <Button>
                                    <Icon type="upload" /> Upload
                                </Button> 1280 x 720 px
                            </Upload>
                              
                        </FormItem>
                        <FormItem 
                            label="Contenu :"
                            validateStatus={this.state.content.validateStatus}
                            help={this.state.content.errorMsg}
                            >
                            <Input.TextArea
                               
                                size="large"
                                name="content"
                                autoComplete="off"
                                placeholder="Le contenu complet ..."
                                value={this.state.content.value} 
                                onChange={(event) => this.handleInputChange(event, this.validateContenu)} />    
                        </FormItem>
                        <FormItem>
                            <Button type="primary" 
                                htmlType="submit" 
                                size="large" 
                                className="signup-form-button"
                                disabled={this.isFormInvalid()}>Sign up</Button>
                            Already registed? <Link to="/login">Login now!</Link>
                        </FormItem>
                    </Form>
                </div>
            </div>
        );
    }

    // Validation Functions

    validateContenu = (content) => {
        if(!content) {
            return {
                validateStatus: false,
                errorMsg: 'Content may not be empty'                
            
            }
        }
        if(content.trim()=="")
        {
            return {
                validateStatus: false,
                errorMsg: 'Content may not be empty' 
            }  

        }
        if(content.length < 20) {
                return {
                    validateStatus: false,
                    errorMsg: 'Content is too short...'                
                }
        }
        return {
                validateStatus: true,
                errorMsg: null   ,           
            }
        }
        
    validateTitle(title){
        if(!title) {
            return {
                validateStatus: false,
                errorMsg: 'Title may not be empty'                
            
            }
        }
        if(title.trim()=="")
        {
            return {
                validateStatus: false,
                errorMsg: 'Title may not be empty' 
            }  

        }
     
        return {
                validateStatus: true,
                errorMsg: null   ,           
            }
    }

   
    validateDescription = (description) => {
        if(!description) {
            return {
                validateStatus: false,
                errorMsg: 'Content may not be empty'                
            
            }
        }
        if(description.trim()=="")
        {
            return {
                validateStatus: false,
                errorMsg: 'Content may not be empty' 
            }  

        }
        if(description.length < 10) {
                return {
                    validateStatus: false,
                    errorMsg: 'Description is too short...'                
                }
        }
        return {
                validateStatus: true,
                errorMsg: null   ,           
            }
    }

   }

export default addEvent;
